void printBstr(char *s, unsigned char *a, unsigned long long l)
{
    unsigned long long i;

    printf("%s", s);

    for (i = 0; i < l; i++)
        printf("%02x", a[i]);

    if (l == 0)
        printf("00");

    printf("\n");
}

